hudson.tasks.Shell.command:
#sudo apt-get -t jessie-backports install golang-go -y
sudo apt-get remove exim4-base -y
sudo DEBIAN_FRONTEND=noninteractive apt-get install -y postfix 
sudo apt-get install postgresql postgresql-9.4 -y
sudo apt-get install nginx-full ntpdate -y
sudo dpkg -i ./output/pitchfork-data*.deb ./output/trident-server*.deb ./output/trident-cli*.deb
sudo su postgres -c "/usr/sbin/tsetup --force-db-destroy setup_test_db"
echo 'include "/etc/trident/nginx/trident-upstream.inc";
server {
        listen 80 default_server;
        listen [::]:80 default_server;
        index index.html index.htm index.nginx-debian.html;
        server_name _;
        include "/etc/trident/nginx/trident-server.inc";
}' > /tmp/default
sudo mv /tmp/default /etc/nginx/sites-enabled/default

echo '
TRIDENT_ENABLED=Yes
DAEMON_USER=trident
DAEMON_OPTS="--insecurecookies"
' > /tmp/default
sudo mv /tmp/default /etc/default/trident

sudo service nginx restart
sudo service trident start
